partials.checkup_results:
{{> checkup_progress progress=100}}
<div class="results-block">
  {{#ifAll @root.request.body.section-1-score @root.request.body.section-2-score @root.request.body.section-3-score @root.request.body.section-4-score @root.request.body.section-5-score ~}}
  <div class="mdc-layout-grid">
    <div class="mdc-layout-grid__inner">
      <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-8-desktop mdc-layout-grid__cell--span-8-tablet mdc-layout-grid__cell--span-4-phone">
        <div class="radarChart"></div>
      </div>
    </div>
  </div>
  {{else}}
  {{> checkup_error}}
  {{/ifAll ~}}
</div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/d3/3.5.6/d3.min.js" charset="utf-8"></script>
<script>
function scrollToResources() {
  document.querySelector('#resources').scrollIntoView({behavior: 'smooth', block: 'start'});
}
/////////////////////////////////////////////////////////
/////////////// The Radar Chart Function ////////////////
/////////////// Written by Nadieh Bremer ////////////////
////////////////// VisualCinnamon.com ///////////////////
/////////// Inspired by the code of alangrafu ///////////
/////////////////////////////////////////////////////////

function RadarChart(id, data, options) {
  var cfg = {
    w: 600,				//Width of the circle
    h: 600,				//Height of the circle
    margin: {top: 5, right: 5, bottom: 5, left: 5}, //The margins of the SVG
    levels: 4,				//How many levels or inner circles should there be drawn
    maxValue: 0, 			//What is the value that the biggest circle will represent
    labelFactor: 1.2, 	//How much farther than the radius of the outer circle should the labels be placed
    wrapWidth: 60, 		//The number of pixels after which a label needs to be given a new line
    opacityArea: 0.25, 	//The opacity of the area of the blob
    dotRadius: 10, 			//The size of the colored circles of each blog
    opacityCircles: 0, 	//The opacity of the circles of each blob
    strokeWidth: 3, 		//The width of the stroke around each blob
    roundStrokes: false,	//If true the area and stroke will follow a round path (cardinal-closed)
    color: d3.scale.category10()	//Color function
  };

  //Put all of the options into a variable called cfg
  if('undefined' !== typeof options){
    for(var i in options){
      if('undefined' !== typeof options[i]){ cfg[i] = options[i]; }
    }//for i
  }//if

  //If the supplied maxValue is smaller than the actual one, replace by the max in the data
  var maxValue = Math.max(cfg.maxValue, d3.max(data, function(i){return d3.max(i.map(function(o){return o.value;}))}));

  var allAxis = (data[0].map(function(i, j){return i.axis})),	//Names of each axis
  total = allAxis.length,					//The number of different axes
  radius = Math.min(cfg.w/2, cfg.h/2), 	//Radius of the outermost circle
  Format = d3.format('%'),			 	//Percentage formatting
  angleSlice = Math.PI * 2 / total;		//The width in radians of each "slice"

  //Scale for the radius
  var rScale = d3.scale.linear()
  .range([0, radius])
  .domain([0, maxValue]);

  //Remove whatever chart with the same id/class was present before
  d3.select(id).select("svg").remove();

  //Initiate the radar chart SVG
  var svg = d3.select(id).append("svg")
  .attr("width",  cfg.w + cfg.margin.left + cfg.margin.right)
  .attr("height", cfg.h + cfg.margin.top + cfg.margin.bottom)
  .attr("class", "radar"+id);
  //Append a g element
  var g = svg.append("g")
  .attr("transform", "translate(" + (cfg.w/2 + cfg.margin.left) + "," + (cfg.h/2 + cfg.margin.top) + ")");

  //Wrapper for the grid & axes
  var axisGrid = g.append("g").attr("class", "axisWrapper");

  //Draw the background circles
  axisGrid.selectAll(".levels")
  .data(d3.range(1,(cfg.levels+1)).reverse())
  .enter()
  .append("circle")
  .attr("class", "gridCircle")
  .attr("r", function(d, i){return radius/cfg.levels*d;})
  .style("fill", "#B8B8B8")
  .style("stroke", "#B8B8B8")
  .style("fill-opacity", cfg.opacityCircles)

  //Create the straight lines radiating outward from the center
  var axis = axisGrid.selectAll(".axis")
  .data(allAxis)
  .enter()
  .append("g")
  .attr("class", "axis");
  //Append the lines
  axis.append("line")
  .attr("x1", 0)
  .attr("y1", 0)
  .attr("x2", function(d, i){ return rScale(maxValue*1.1) * Math.cos(angleSlice*i - Math.PI/2); })
  .attr("y2", function(d, i){ return rScale(maxValue*1.1) * Math.sin(angleSlice*i - Math.PI/2); })
  .attr("class", "line")
  .style("stroke", "#B8B8B8")
  .style("stroke-width", "1px");

  //Append the labels at each axis
  axis.append("text")
  .attr("class", "legend")
  .style("font-size", "1rem")
  .style("font-variant", "all-small-caps")
  .attr("text-anchor", "middle")
  .attr("dy", "0.35em")
  .attr("x", function(d, i){ return rScale(maxValue * cfg.labelFactor) * Math.cos(angleSlice*i - Math.PI/2); })
  .attr("y", function(d, i){ return rScale(maxValue * cfg.labelFactor) * Math.sin(angleSlice*i - Math.PI/2); })
  .text(function(d){return d})
  .call(wrap, cfg.wrapWidth);

  /////////////////////////////////////////////////////////
  ///////////// Draw the radar chart blobs ////////////////
  /////////////////////////////////////////////////////////

  //The radial line function
  var radarLine = d3.svg.line.radial()
  .interpolate("linear-closed")
  .radius(function(d) { return rScale(d.value); })
  .angle(function(d,i) {	return i*angleSlice; });

  if(cfg.roundStrokes) {
    radarLine.interpolate("cardinal-closed");
  }

  //Create a wrapper for the blobs
  var blobWrapper = g.selectAll(".radarWrapper")
  .data(data)
  .enter().append("g")
  .attr("class", "radarWrapper");

  //Append the backgrounds
  blobWrapper
  .append("path")
  .attr("class", "radarArea")
  .attr("d", function(d,i) { return radarLine(d); })
  .style("fill", function(d,i) { return cfg.color(i); })
  .style("fill-opacity", function(d,i) { return cfg.fillOpacity(i); })

  //Create the outlines
  blobWrapper.append("path")
  .attr("class", "radarStroke")
  .attr("d", function(d,i) { return radarLine(d); })
  .style("stroke-width", cfg.strokeWidth + "px")
  .style("stroke-dasharray", function(d,i) { return cfg.strokeStyle(i); })
  .style("stroke", function(d,i) { return cfg.color(i); })
  .style("fill", "none")
  // .style("filter" , "url(#glow)");

  //Append the circles
  blobWrapper.selectAll(".radarCircle")
  .data(function(d,i) { return d; })
  .enter().append("circle")
  .attr("class", "radarCircle")
  .attr("r", cfg.dotRadius)
  .attr("cx", function(d,i){ return rScale(d.value) * Math.cos(angleSlice*i - Math.PI/2); })
  .attr("cy", function(d,i){ return rScale(d.value) * Math.sin(angleSlice*i - Math.PI/2); })
  .style("stroke-width", cfg.strokeWidth + "px")
  .style("stroke-dasharray", function(d,i,j) { return cfg.strokeStyle(j); })
  .style("stroke", function(d,i,j) { return cfg.color(j); })
  .style("fill", function(d,i,j) { return cfg.dotColor(j); })
  .style("fill-opacity", 1);

  //Taken from http://bl.ocks.org/mbostock/7555321
  //Wraps SVG text
  function wrap(text, width) {
    text.each(function() {
      var text = d3.select(this),
      words = text.text().split(/\s+/).reverse(),
      word,
      line = [],
      lineNumber = 0,
      lineHeight = 1.4, // ems
      y = text.attr("y"),
      x = text.attr("x"),
      dy = parseFloat(text.attr("dy")),
      tspan = text.text(null).append("tspan").attr("x", x).attr("y", y).attr("dy", dy + "em");

      while (word = words.pop()) {
        line.push(word);
        tspan.text(line.join(" "));
        if (tspan.node().getComputedTextLength() > width) {
          line.pop();
          tspan.text(line.join(" "));
          line = [word];
          tspan = text.append("tspan").attr("x", x).attr("y", y).attr("dy", ++lineNumber * lineHeight + dy + "em").text(word);
        }
      }
    });
  }//wrap

}//RadarChart
</script>
<script>

/* Radar chart design created by Nadieh Bremer - VisualCinnamon.com */

//////////////////////////////////////////////////////////////
//////////////////////// Set-Up //////////////////////////////
//////////////////////////////////////////////////////////////

var margin = {top: 75, right: 75, bottom: 20, left: 75},
width = Math.min(700, window.innerWidth - 10) - margin.left - margin.right,
height = Math.min(width, window.innerHeight - margin.top - margin.bottom - 20);

//////////////////////////////////////////////////////////////
////////////////////////// Data //////////////////////////////
//////////////////////////////////////////////////////////////

var data = [
  [//Average scores
    {
      axis:"{{{@root.route.metaData.governanceCheckup.section-1.name}}}",
      value:{{@root.request.body.section-1-score}}/100
    },
    {
      axis:"{{{@root.route.metaData.governanceCheckup.section-2.name}}}",
      value:{{@root.request.body.section-2-score}}/100
    },
    {
      axis:"{{{@root.route.metaData.governanceCheckup.section-3.name}}}",
      value:{{@root.request.body.section-3-score}}/100
    },
    {
      axis:"{{{@root.route.metaData.governanceCheckup.section-4.name}}}",
      value:{{@root.request.body.section-4-score}}/100
    },
    {
      axis:"{{{@root.route.metaData.governanceCheckup.section-5.name}}}",
      value:{{@root.request.body.section-5-score}}/100
    }
  ]
];
//////////////////////////////////////////////////////////////
//////////////////// Draw the Chart //////////////////////////
//////////////////////////////////////////////////////////////

var color = d3.scale.ordinal()
.range(["#444444","#1976D2"]);
var dotColor = d3.scale.ordinal()
.range(["#ffffff","#1976D2"]);
var fillOpacity = d3.scale.ordinal()
.range(["0","0.1"]);
var strokeStyle = d3.scale.ordinal()
.range(["4,3","0"]);

var radarChartOptions = {
  w: width,
  h: height,
  margin: margin,
  maxValue: 0,
  levels: 5,
  roundStrokes: true,
  color: color,
  dotColor: dotColor,
  fillOpacity: fillOpacity,
  strokeStyle: strokeStyle
};
//Call function to draw the Radar chart
RadarChart(".radarChart", data, radarChartOptions);
</script>

partials.checkup_scores:
{{#if query_params ~}}
  {{#each (keyValue query_params) ~}}
  <input type="hidden" name="{{key}}" value="{{value}}" />
  {{/each ~}}
{{else}}
  {{#if @root.request.body ~}}
    {{#if @root.request.body.scores-organisation_type ~}}
      <input type="hidden" name="organisation_type" value="{{@root.request.body.scores-organisation_type}}" />
    {{/if ~}}
    {{#if @root.request.body.section-1-score ~}}
      <input type="hidden" name="section-1-score" value="{{@root.request.body.section-1-score}}" />
    {{/if ~}}
    {{#if @root.request.body.section-2-score ~}}
      <input type="hidden" name="section-2-score" value="{{@root.request.body.section-2-score}}" />
    {{/if ~}}
    {{#if @root.request.body.section-3-score ~}}
      <input type="hidden" name="section-3-score" value="{{@root.request.body.section-3-score}}" />
    {{/if ~}}
    {{#if @root.request.body.section-4-score ~}}
      <input type="hidden" name="section-4-score" value="{{@root.request.body.section-4-score}}" />
    {{/if ~}}
    {{#if @root.request.body.section-5-score ~}}
      <input type="hidden" name="section-5-score" value="{{@root.request.body.section-5-score}}" />
    {{/if ~}}
    {{#ifAny @root.request.body.scores @root.request.body.scores-q1 ~}}
      <input type="hidden" name="{{section}}-score" value="{{add
          (add
            (add
              (sumArray
                (arrayify @root.request.body.scores)
              )
              (sumArray
                (arrayify @root.request.body.scores-q1)
              )
            )
            (add
              (sumArray
                (arrayify @root.request.body.scores-q2)
              )
              (sumArray
                (arrayify @root.request.body.scores-q3)
              )
            )
          )
          (add
            (sumArray
              (arrayify @root.request.body.scores-q4)
            )
            (sumArray
              (arrayify @root.request.body.scores-q5)
            )
          )}}" />
    {{else}}
      <input type="hidden" name="{{section}}-score" value="0" />
    {{/ifAny ~}}
    {{!-- {{#compare section "section-6" ~}}
      <input type="hidden" name="income_band" value="{{@root.request.body.scores-income_band}}" />
      <input type="hidden" name="topic" value="{{@root.request.body.scores-topic}}" />
    {{else}}
      {{#if @root.request.body.income_band ~}}
        <input type="hidden" name="income_band" value="{{@root.request.body.income_band}}" />
      {{/if ~}}
      {{#if @root.request.body.topic ~}}
        <input type="hidden" name="topic" value="{{@root.request.body.topic}}" />
      {{/if ~}}
    {{/compare ~}} --}}
  {{else}}
    <input type="hidden" name="{{section}}-score" value="0" />
  {{/if ~}}
{{/if ~}}

