              </div>
              {{/ifAny ~}}
              {{/if ~}}
              {{/case ~}}
              {{/switch ~}}
              {{/each ~}}
            </div>
            {{/ifAny ~}}
          {{!-- </div> --}}
        </div>
        {{/each ~}}
      {{!-- </div> --}}
      <div class="cta b-padding-full">
        <button type="submit" class="mdc-button mdc-button--raised">Next section</button>
      </div>
    </div>
  </form>
  {{/with ~}}
{{/compare ~}}

